import React, { useState, useEffect, useCallback } from 'react';
import { 
  LineChart, Line, XAxis, YAxis, CartesianGrid, 
  Tooltip, Legend, ResponsiveContainer 
} from 'recharts';
import SensorController from '../../controllers/SensorController';
import './SensorChart.css';

const AdafruitFeedChart = ({ feedType, title, timeRange = 'day' }) => {
  const [chartData, setChartData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  
  const loadChartData = useCallback(async () => {
    setIsLoading(true);
    try {
      // Calculate date range based on timeRange
      let startDate = null;
      const today = new Date();
      
      if (timeRange === 'day') {
        // Set to 1 minute ago instead of 24 hours ago
        startDate = new Date(today);
        startDate.setMinutes(today.getMinutes() - 1);
      } else if (timeRange === 'week') {
        // Set to 7 days ago
        startDate = new Date(today);
        startDate.setDate(startDate.getDate() - 7);
      } else if (timeRange === 'month') {
        // Set to 30 days ago
        startDate = new Date(today);
        startDate.setDate(startDate.getDate() - 30);
      }
      
      // Format dates as YYYY-MM-DD
      const formattedStartDate = startDate ? startDate.toISOString() : null;
      const formattedEndDate = today.toISOString();
      
      // Get feed data from Adafruit IO - for 'day' only get last 10 records instead of 50
      const limit = timeRange === 'day' ? 10 : timeRange === 'week' ? 100 : 200;
      const data = await SensorController.getFeedDataByDate(
        feedType, 
        formattedStartDate, 
        formattedEndDate, 
        limit, 
        true
      );
      
      setChartData(data);
    } catch (error) {
      console.error(`Error loading ${feedType} feed data:`, error);
    } finally {
      setIsLoading(false);
    }
  }, [feedType, timeRange]);
  
  useEffect(() => {
    loadChartData();
    
    // Auto-refresh data every minute
    const interval = setInterval(() => {
      loadChartData();
    }, 60000);
    
    return () => clearInterval(interval);
  }, [loadChartData]);
  
  const formatTimestamp = (timestamp) => {
    if (!timestamp) return '';
    const date = new Date(timestamp);
    
    if (timeRange === 'day') {
      // For day view, show time in HH:MM format
      return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    } else if (timeRange === 'week') {
      // For week view, show date with day of week
      return date.toLocaleDateString([], {
        weekday: 'short',
        month: 'numeric',
        day: 'numeric'
      });
    } else {
      // For month view, show date only
      return date.toLocaleDateString([], {
        month: 'numeric',
        day: 'numeric'
      });
    }
  };
  
  // Get appropriate unit based on feed type
  const getUnit = () => {
    switch (feedType) {
      case 'temperature':
        return '°C';
      case 'humidity':
        return '%';
      case 'fan':
      case 'light':
        return '';
      default:
        return '';
    }
  };
  
  // Get appropriate color based on feed type
  const getColor = () => {
    switch (feedType) {
      case 'temperature':
        return '#FF5733';
      case 'humidity':
        return '#3498DB';
      case 'fan':
        return '#2ECC71';
      case 'light':
        return '#F39C12';
      default:
        return '#95a5a6';
    }
  };
  
  if (isLoading && chartData.length === 0) {
    return (
      <div className="sensor-chart">
        <div className="chart-header">
          <h3>{title || `${feedType.charAt(0).toUpperCase() + feedType.slice(1)} Chart`}</h3>
        </div>
        <div className="chart-loading">Loading data...</div>
      </div>
    );
  }
  
  return (
    <div className="sensor-chart">
      <div className="chart-header">
        <h3>{title || `${feedType.charAt(0).toUpperCase() + feedType.slice(1)} Chart`}</h3>
      </div>
      
      <div className="chart-description">
        Showing data from Adafruit IO feed for the last minute
      </div>
      
      <div className="chart-container">
        {chartData.length > 0 ? (
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={chartData} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis 
                dataKey="timestamp" 
                tickFormatter={formatTimestamp}
                tick={{ fontSize: 12 }}
                interval="preserveStartEnd"
              />
              <YAxis 
                domain={['auto', 'auto']}
                label={{ value: getUnit(), angle: -90, position: 'insideLeft' }}
              />
              <Tooltip 
                labelFormatter={formatTimestamp}
                formatter={(value) => [`${value}${getUnit()}`, feedType]}
              />
              <Legend />
              <Line 
                type="monotone" 
                dataKey="value" 
                stroke={getColor()} 
                activeDot={{ r: 8 }} 
                name={feedType.charAt(0).toUpperCase() + feedType.slice(1)}
                strokeWidth={2}
              />
            </LineChart>
          </ResponsiveContainer>
        ) : (
          <div className="no-data">No data available for this time range</div>
        )}
      </div>
    </div>
  );
};

export default AdafruitFeedChart;
